import React, { useEffect, useState } from 'react';
import Form from './components/Form';
import Card from './components/Card';

import './App.css';

function App() {
  const [state, setState] = useState({
    cardName: '',
    cardDescription: '',
    cardImage: '',
    cardAttr1: '0',
    cardAttr2: '0',
    cardAttr3: '0',
    cardRare: 'normal',
    cardTrunfo: false,
    isSaveButtonDisabled: true,
  });

  const [cards, setCards] = useState([]);

  const onSaveButtonClick = () => {
    setCards((prevState) => [...prevState, state]);

    setState({
      cardName: '',
      cardDescription: '',
      cardImage: '',
      cardAttr1: '0',
      cardAttr2: '0',
      cardAttr3: '0',
      cardRare: 'normal',
      cardTrunfo: false,
      isSaveButtonDisabled: true,
    });
  };

  useEffect(() => {
    const isValid = () => {
      const maxAttr = 90;
      const sumMaxAttr = 210;

      if (state.cardName === ''
      || state.cardDescription === ''
      || state.cardImage === '') return false;

      if (state.cardAttr1 <= 0
        || state.cardAttr2 <= 0
        || state.cardAttr3 <= 0) return false;

      if (state.cardAttr1 > maxAttr
        || state.cardAttr2 > maxAttr
        || state.cardAttr3 > maxAttr) return false;

      return (Number(state.cardAttr1) + Number(state.cardAttr2) + Number(state.cardAttr3)
      < sumMaxAttr);
    };

    const validState = isValid();
    console.log(validState);

    if (state.isSaveButtonDisabled && validState) {
      setState((prevState) => ({ ...prevState, isSaveButtonDisabled: false }));
    } else if (!state.isSaveButtonDisabled && !validState) {
      setState((prevState) => ({ ...prevState, isSaveButtonDisabled: true }));
    }
  }, [state]);

  return (
    <>
      <header className="flex my-60 justify-center">
        <div className="box-header liner-gradient shadow rounded-sm">
          <h1
            className="box-header--inner
              px-20 text-7 font-bold uppercase liner-gradient my-25"
          >
            Tryunfo
          </h1>
        </div>
      </header>

      <div className="flex mx-60 p-50 bg-white rounded-sm">
        <Form
          { ...state }
          onInputChange={ setState }
          onSaveButtonClick={ onSaveButtonClick }
        />

        <Card { ...state } />

        {cards.map((card) => <p key={ card.cardName }>{card.cardName}</p>)}
      </div>

    </>
  );
}

export default App;
